/**
 * Utility functions for the Time-Blocked To-Do Calendar App
 */

/**
 * Generate a UUID (v4)
 * @returns {string} UUID string
 */
export function generateUUID() {
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
    const r = Math.random() * 16 | 0;
    const v = c === 'x' ? r : (r & 0x3 | 0x8);
    return v.toString(16);
  });
}

/**
 * Parse time string in HH:mm format
 * @param {string} timeStr - Time string in HH:mm format
 * @returns {{h: number, m: number}} Object with hours and minutes
 * @throws {Error} If time format is invalid
 */
export function parseTime(timeStr) {
  if (!timeStr || typeof timeStr !== 'string') {
    throw new Error('Time string is required');
  }

  const parts = timeStr.split(':');
  if (parts.length !== 2) {
    throw new Error('Invalid time format. Expected HH:mm');
  }

  const h = parseInt(parts[0], 10);
  const m = parseInt(parts[1], 10);

  if (isNaN(h) || isNaN(m)) {
    throw new Error('Time components must be numbers');
  }

  if (h < 0 || h > 23 || m < 0 || m > 59) {
    throw new Error('Invalid time values: hours must be 0-23, minutes must be 0-59');
  }

  return { h, m };
}

/**
 * Format date to YYYY-MM-DD string
 * @param {Date} date - Date object
 * @returns {string} Formatted date string
 */
export function formatDate(date) {
  const year = date.getFullYear();
  const month = String(date.getMonth() + 1).padStart(2, '0');
  const day = String(date.getDate()).padStart(2, '0');
  return `${year}-${month}-${day}`;
}

/**
 * Get current date as YYYY-MM-DD string
 * @returns {string} Current date string
 */
export function getCurrentDateStr() {
  return formatDate(new Date());
}

/**
 * Convert time string to minutes from midnight
 * @param {string} timeStr - Time string in HH:mm format
 * @returns {number} Minutes from midnight
 */
export function minutesFromMidnight(timeStr) {
  const { h, m } = parseTime(timeStr);
  return h * 60 + m;
}

/**
 * Format time for display (24-hour format)
 * @param {string} timeStr - Time string in HH:mm format
 * @returns {string} Formatted time string
 */
export function formatTimeDisplay(timeStr) {
  const { h, m } = parseTime(timeStr);
  const hStr = String(h).padStart(2, '0');
  const mStr = String(m).padStart(2, '0');
  return `${hStr}:${mStr}`;
}

/**
 * Convert 24-hour time to 12-hour format with AM/PM
 * @param {string} timeStr - Time string in HH:mm format
 * @returns {string} Formatted time string (e.g., "4:45 AM")
 */
export function formatTime12Hour(timeStr) {
  const { h, m } = parseTime(timeStr);
  const suffix = h >= 12 ? 'PM' : 'AM';
  const h12 = h % 12 || 12; // Convert 0 to 12
  const mStr = String(m).padStart(2, '0');
  return `${h12}:${mStr} ${suffix}`;
}

/**
 * Validate time string format
 * @param {string} timeStr - Time string to validate
 * @returns {boolean} True if valid
 */
export function isValidTimeFormat(timeStr) {
  if (!timeStr || typeof timeStr !== 'string') {
    return false;
  }

  const timeRegex = /^([01]\d|2[0-3]):([0-5]\d)$/;
  return timeRegex.test(timeStr);
}

/**
 * Get date string for N days from today
 * @param {number} daysOffset - Number of days offset (positive or negative)
 * @returns {string} Date string in YYYY-MM-DD format
 */
export function getDateOffset(daysOffset) {
  const date = new Date();
  date.setDate(date.getDate() + daysOffset);
  return formatDate(date);
}

/**
 * Parse ISO date string to Date object
 * @param {string} dateStr - Date string in YYYY-MM-DD format
 * @returns {Date} Date object
 */
export function parseDate(dateStr) {
  if (!dateStr || typeof dateStr !== 'string') {
    throw new Error('Date string is required');
  }

  const parts = dateStr.split('-');
  if (parts.length !== 3) {
    throw new Error('Invalid date format. Expected YYYY-MM-DD');
  }

  const year = parseInt(parts[0], 10);
  const month = parseInt(parts[1], 10) - 1; // Months are 0-indexed
  const day = parseInt(parts[2], 10);

  if (isNaN(year) || isNaN(month) || isNaN(day)) {
    throw new Error('Date components must be numbers');
  }

  return new Date(year, month, day);
}

/**
 * Format date for display (e.g., "June 15, 2024")
 * @param {string} dateStr - Date string in YYYY-MM-DD format
 * @returns {string} Formatted date string
 */
export function formatDateDisplay(dateStr) {
  const date = parseDate(dateStr);
  const options = { year: 'numeric', month: 'long', day: 'numeric' };
  return date.toLocaleDateString('en-US', options);
}
